import React, { useState } from "react";
import Dashboard from "./dashboard";

const Home = () => {
  const [colorno, setColor] = useState(0);
  
  const changecolor = () => {
    setColor((prev) => !prev);
  };

  const bodycolor = {
    backgroundColor: colorno ? "black" : "#f1f4dc",
    color: colorno ? "white" : "black",
    height: "100vh",
    transition: "background-color 0.5s, color 0.5s"
  };

  return (
    <div style={bodycolor} className="container">
      <Dashboard oncolor={changecolor} colorno={colorno} />
      <h1>Home Page</h1>
      <p>Welcome to the Home Page</p>
      <section className="description">
        <h2>About Rise Up</h2>
        <p>
          Rise Up is your personal task management platform. Complete your daily tasks and earn coins! Use your coins in our shop platform to get exciting rewards. Stay productive and rise up to the challenges every day!
        </p>
      </section>
    </div>
  );
};

export default Home;
