import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [name, setname] = useState('');
  const [wakeuptime, setwakeuptime] = useState('');
  const [sleeptime, setsleeptime] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/register', new URLSearchParams({ name, username, password, wakeuptime, sleeptime }));
      if (response.status === 200) {
        window.location.href = '/';
      } else {
        alert('Registration failed');
      }
    } catch (error) {
      console.error('Error during registration:', error);
      alert('Registration failed');
    }
  };

  const validateWakeUpTime = (time) => {
    const [hour, minute] = time.split(':').map(Number);
    return hour >= 3 && hour <= 7;
  };

  return (
    <div>
      <h1>Register</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
        <input
          type="text"
          value={name}
          onChange={(e) => setname(e.target.value)}
          placeholder="Name"
        />
        </label>
        
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
        />
       <label>
          Wake Up Time (between 3:00 AM and 7:00 AM):
          <input
            type="time"
            value={wakeuptime}
            onChange={(e) => {
              if (validateWakeUpTime(e.target.value)) {
                setwakeuptime(e.target.value);
              } else {
                alert('Wake up Early :)');
              }
            }}
          />
        </label>
        <label>
        Sleep Time:
        <input
          type="time"
          value={sleeptime}
          onChange={(e) => setsleeptime(e.target.value)}
        />
        </label>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
